package co.edu.unac.poo1.alimentos;import co.edu.unac.poo1.procesos.Madurado;public abstract class Alimentos implements Madurado, AccesorioCocina {    protected String nombre;    protected Float tamaho;    protected String sabor;    protected Float precio;    protected String serial;    public Alimentos() {    }    public Alimentos(String nombre, Float tamaho, String sabor, Float precio, String serial) {        this.nombre = nombre;        this.tamaho = tamaho;        this.sabor = sabor;        this.precio = precio;        this.serial = serial;    }    public Float getPrecio() {        return precio;    }    public void setPrecio(Float precio) {        this.precio = precio;    }    public String getSerial() {        return serial;    }    public void setSerial(String serial) {        this.serial = serial;    }    public String getNombre() {        return nombre;    }    public void setNombre(String nombre) {        this.nombre = nombre;    }    public Float getTamaho() {        return tamaho;    }    public void setTamaho(Float tamaho) {        this.tamaho = tamaho;    }    public String getSabor() {        return sabor;    }    public void setSabor(String sabor) {        this.sabor = sabor;    }}