package co.edu.unac.poo1.alimentos;import co.edu.unac.poo1.procesos.Madurado;public class Arepa extends Alimentos {    private String relleno;    public Arepa() {        super();    }    public Arepa(String nombre, Float tamaho, String sabor, Float precio, String serial, String relleno) {        super(nombre, tamaho, sabor, precio, serial);        this.relleno = relleno;    }    public String getRelleno() {        return relleno;    }    public void setRelleno(String relleno) {        this.relleno = relleno;    }    @Override    public boolean hornear() {        return false;    }    @Override    public boolean tostar() {        return false;    }    @Override    public String getSerialProductos() {        return this.serial;    }    @Override    public void verInformacion(){        System.out.println("-----------------------");        System.out.println("serial arepas: " + this.serial);        System.out.println("Nombre arepas: " + this.nombre);        System.out.println("-----------------------");    }}