package co.edu.unac.poo1.alimentos;public class Mazorca extends Alimentos {    private Integer totalGranos;    private Float peso;    private String color;    public Mazorca() {        super();    }    public Mazorca(String nombre, Float tamaho, String sabor, Float precio, String serial, Integer totalGranos, Float peso, String color) {        super(nombre, tamaho, sabor, precio, serial);        this.totalGranos = totalGranos;        this.peso = peso;        this.color = color;    }    public Integer getTotalGranos() {        return totalGranos;    }    public void setTotalGranos(Integer totalGranos) {        this.totalGranos = totalGranos;    }    public void setPeso(Float peso) {        this.peso = peso;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    @Override    public boolean hornear() {        return false;    }    @Override    public boolean tostar() {        return false;    }    @Override    public String getSerialProductos() {        return this.serial;    }    @Override    public void verInformacion(){        System.out.println("-----------------------");        System.out.println("Nombre Mazorcas: " + this.nombre);        System.out.println("precio Mazorcas: " + this.precio);        System.out.println("serial Mazorcas: " + this.serial);        System.out.println("-----------------------");    }}