package co.edu.unac.poo1.alimentos;public class PandeBono extends Alimentos {    protected Integer tiempoCoccion;    private String color;    public PandeBono() {        super();    }    public PandeBono(String nombre, Float tamaho, String sabor, Float precio, String serial, Integer tiempoCoccion, String color) {        super(nombre, tamaho, sabor, precio, serial);        this.tiempoCoccion = tiempoCoccion;        this.color = color;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    public Integer getTiempoCoccion() {        return tiempoCoccion;    }    public void setTiempoCoccion(Integer tiempoCoccion) {        this.tiempoCoccion = tiempoCoccion;    }    @Override    public boolean hornear() {        return false;    }    @Override    public boolean tostar() {        return false;    }    @Override    public String getSerialProductos() {        return this.serial;    }    @Override    public void verInformacion(){        System.out.println("-----------------------");        System.out.println("Nombre pandeBono: " + this.nombre);        System.out.println("Precio pandeBono: " + this.precio);        System.out.println("serial pandeBono: " + this.serial);        System.out.println("-----------------------");    }}